import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

/**
 * 2초 지연을 시키는 함수입니다 (비동기 작업).
 */
import { waitTwoSeconds } from "../../utils";

export const __addToDo = createAsyncThunk(
  "__addToDo", async (payload, thunkAPI) => {
  try {
    await waitTwoSeconds();
    const newTodo = { id: nextId(), title, body };
    return newTodo;
  } catch (error) {
    return rejectWithValue(error.message);
  }
});

export const __deleteTodo = createAsyncThunk(
  "__deleteToDo", async (payload, thunkAPI) => {
  try {
    await waitTwoSeconds();
    return todoId;
  } catch (error) {
    return rejectWithValue(error.message);
  }
});

const initialState = {
  list: [],
};

const todosSlice = createSlice({
  name: "todos",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(__addToDo.fulfilled, (state, action) => {
        state.list.push(action.payload); // Add the new todo to the list
      })
      .addCase(__deleteTodo.fulfilled, (state, action) => {
        const index = state.list.findIndex(todo => todo.id === action.payload);
        if (index !== -1) state.list.splice(index, 1); // Remove the todo by id
      });
  },
});

export const { addTodo, deleteTodo } = todosSlice.actions;
export default todosSlice.reducer;
